CREATE TABLE citas (id INT AUTO_INCREMENT NOT NULL, fe_creacion DATETIME NOT NULL, motivo VARCHAR(255) NOT NULL, estado VARCHAR(255) NOT NULL, tipo_cita_id INT DEFAULT NULL, paciente_id INT NOT NULL, medico_id INT DEFAULT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE facturas (id INT AUTO_INCREMENT NOT NULL, fe_creacion DATETIME NOT NULL, total DOUBLE PRECISION NOT NULL, cita_id INT NOT NULL, cajero_id INT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE tipos_citas (id INT AUTO_INCREMENT NOT NULL, descripcion VARCHAR(255) NOT NULL, costo DOUBLE PRECISION NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
CREATE TABLE `user` (id INT AUTO_INCREMENT NOT NULL, email VARCHAR(180) NOT NULL, roles JSON NOT NULL, password VARCHAR(255) NOT NULL, nombres VARCHAR(255) NOT NULL, apellidos VARCHAR(255) DEFAULT NULL, tipo_usuario VARCHAR(255) NOT NULL, UNIQUE INDEX UNIQ_8D93D649E7927C74 (email), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8mb4 COLLATE `utf8mb4_unicode_ci` ENGINE = InnoDB;
